import React from "react";
import { withStyles } from "@material-ui/core/styles";
import TableCell from "@material-ui/core/TableCell";
import TableRow from "@material-ui/core/TableRow";
import { inject, observer } from "mobx-react";
import DeleteIcon from "@material-ui/icons/Delete";
import PlaylistPlayIcon from "@material-ui/icons/PlaylistPlay";

import Details from "./Details";

const dateFormat = require("dateformat");
const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.primary.main,
    color: theme.palette.secondary.main,
    fontSize: 16,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    "&:nth-of-type(odd)": {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

const TasksInfo = inject("tasksManager")(
  observer((props) => {
    const name = props.name;
    const startDate = props.startDate;
    const lastUpdate = props.lastUpdate;
    const id = props.id;


   

    

 

    return (
      <>
        <StyledTableRow>
          <StyledTableCell component="th" scope="row">
            {name}
          </StyledTableCell>
          <StyledTableCell align="right">
            {dateFormat(startDate, "dd/mm/yyyy")}
          </StyledTableCell>
          <StyledTableCell align="right">
            {dateFormat(lastUpdate, "dd/mm/yyyy")}
          </StyledTableCell>
          <StyledTableCell align="left">
            <DeleteIcon onClick={()=>props.handeldeleteTask(id)} cursor="pointer" />
              <PlaylistPlayIcon cursor="pointer" onClick={props.handleClickOpen}  />
          </StyledTableCell>
        </StyledTableRow>
        <Details  open={props.state.open} handleClose={props.handleClickClose} taskId={id} taskName={name} />
      </>
    );
  })
);
export default TasksInfo;
