import React,{useEffect} from "react";
import { observer, inject } from "mobx-react";
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import Badge from '@material-ui/core/Badge';
import MenuItem from '@material-ui/core/MenuItem';
import AssignmentIcon from '@material-ui/icons/Assignment';
import AddTask from "../components/AddTask";






const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  toolbar:{
     justifyContent:"flex-end",
     flexDirection:"row",
    

  },
}));

const TasksList = inject("tasksManager")(
  observer((props) => {

  const classes = useStyles();

    
    useEffect(() => {
      (async () => {
        await props.tasksManager.getTasks();
      })();
    }, []);

  const [state, setState] = useState({
    task: {
      name: "",
      startDate: dateFormat(now, "yyyy-mm-dd'T'HH:MM:ss"),
      lastUpdate: dateFormat(now, "yyyy-mm-dd'T'HH:MM:ss"),
    },
    details: {},
    alerts: {},
  });
  
  const handleChange=(e,name)=>{
  console.log( e.target.value)
  
  }
  
    return (
      <>
      <div className={classes.root}>
        <AppBar position="static">
          <Toolbar  className={classes.toolbar} variant="dense">
            <Typography style={{marginRight:'34vw'}}  variant="h5" color="inherit">
            To Do
            </Typography>
            <MenuItem>
        <IconButton aria-label="show 4 new mails" color="inherit">
          <Badge badgeContent={props.tasksManager.tasksSum} color="secondary">
            <AssignmentIcon />
          </Badge>
        </IconButton>
        <p>Open</p>
      </MenuItem>
          </Toolbar>
        </AppBar>
      </div>
      <AddTask />
      </>
    );

   



  })
);
export default TasksList;
