import React from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import { inject, observer } from "mobx-react";
import Grid from "@material-ui/core/Grid";
import Taskinfo from "./Taskinfo";
import QueuePlayNextIcon from "@material-ui/icons/QueuePlayNext";


const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.primary.main,
    color: theme.palette.secondary.main,
    fontSize: 14,
    padding: 5,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const useStyles = makeStyles({
  table: {
    maxWidth: 200,
  },
});

const TasksList = inject("tasksManager")(
  observer((props) => {
    const classes = useStyles();
    const tasks = props.tasksManager.tasks;
    
    return (
      <Grid
        style={{ marginTop: "25px" }}
        container
        spacing={1}
        direction="column"
        justify="center"
        alignItems="center"
      >
        <Grid item xs={12}>
          <TableContainer component={Paper}>
            <Table className={classes.table} aria-label="customized table">
              <TableHead>
                <TableRow>
                  <StyledTableCell align="left">Description</StyledTableCell>
                  <StyledTableCell align="left">Start Date</StyledTableCell>
                  <StyledTableCell align="left">Last Update</StyledTableCell>
                  <StyledTableCell align="justify">Actions</StyledTableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {tasks.map((task) => (
                  <Taskinfo setState={props.setState}
                  handeldeleteTask={props.handeldeleteTask}
                    id={task.id}
                    key={task.id}
                    name={task.name}
                    startDate={task.startDate}
                    lastUpdate={task.lastUpdate}
                  />
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </Grid>
      </Grid>
    );
  })
);
export default TasksList;
