import React,{useEffect,useState} from "react";
import { observer, inject } from "mobx-react";
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import Badge from '@material-ui/core/Badge';
import MenuItem from '@material-ui/core/MenuItem';
import AssignmentIcon from '@material-ui/icons/Assignment';
import AddTask from "./Add";
import TaskList from './TasksList'
const dateFormat = require("dateformat");
const now = new Date();




const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  toolbar:{
     justifyContent:"space-between",
     flexDirection:"row",
    

  },
}));

const FirstPage = inject("tasksManager")(
  observer((props) => {

  
  const classes = useStyles();
  function capitalizeFirstLetter(string){
    return string.charAt(0).toUpperCase() + string.slice(1)
  }
    
   /*  useEffect(() => {
      (async () => {
        await props.tasksManager.getTasks();
      })();
    }, []); */

    const [state, setState] = useState({
    task: {
      name: "",
      startDate: dateFormat(now, "yyyy-mm-dd'T'HH:MM:ss"),
      lastUpdate: dateFormat(now, "yyyy-mm-dd'T'HH:MM:ss"),
    },
    detail: {},
    alerts: {},
  });
  
  const handleChange=(e,type)=>{
  const name = e.target.value
  setState({...state,[type]:{...state[type],name:capitalizeFirstLetter(name)}})
  }

  const handlClick= async(type)=>{
    await props.tasksManager.addTask(state[type])
    setState({...state,[type]:{...state[type],name:''}})
    
  }
  console.log(state)
    return (
      <>
      <div className={classes.root}>
        <AppBar position="static">
          <Toolbar  className={classes.toolbar} variant="dense">
            <Typography   variant="h5" color="inherit">
            To Do
            </Typography>
            <MenuItem>
        <IconButton aria-label="show 4 new mails" color="inherit">
          <Badge badgeContent={props.tasksManager.tasksSum} color="secondary">
            <AssignmentIcon />
          </Badge>
        </IconButton>
        <p>Open</p>
      </MenuItem>
          </Toolbar>
        </AppBar>
      </div>
      <AddTask state={state}  name={'task'} handleChange={handleChange} handlClick={handlClick} />

      </>
    );

   



  })
);
export default FirstPage;
